import React, { Component, useState, useEffect } from 'react';
import './style.css';
import AppNavbar from './AppNavbar';
import { Link } from 'react-router-dom';
import { Button, Container } from 'reactstrap';
import Overview from "./pedidosList/Overview";
import Add from "./pedidosList/Add";

function Home() {

    const PARCEL_ARRAY = [{}];
    const SHIP_NUMBER = "";
    const COMPANY = "";
    const EMPTY = "true";

    const [parcel_array, setParcelArray] = useState (PARCEL_ARRAY);
    const [ship_number, setShipNumber] = useState(SHIP_NUMBER);
    const [company, setCompany] = useState (COMPANY);
    const [empty, setEmpty] = useState(EMPTY);

    useEffect(() => {
        // Update the document title using the browser API
        document.title = "Home";
    });

    function handleShipNumber(a){
        setShipNumber(a);
    }

    function handleCompany(a){
        //setCompany(a);
    }

    async function uploadParcel(){
        const options = {
            method: 'POST',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'aftership-api-key': 'cb88e797-1fef-4db3-9a49-ac05adcea15a'
            },
            body: JSON.stringify({tracking: {tracking_number: ship_number}})
        };

        const response = await fetch('https://api.aftership.com/v4/trackings', options);
        const myjson = await response.json();
        setCompany(myjson.data.tracking.slug);
    }

    function saveParcel(){
        var shipment = {"shipnumber" : ship_number, "company" : company};
        var array = [...parcel_array, shipment];
        setParcelArray(array);
        setShipNumber("");
        setCompany("");
        setEmpty(false);
    }

    return (
        <div>

            <div class="row">
                <button class="ghost">Pedidos</button>
                <button>Pagos y aduanas</button>
                <button class="ghost" onclick="location.href='Contacto.html'"> Contacto</button>
            </div>

            <div>&nbsp</div>

            <div>
                <image src="Imagenes/32360.png" height="35" ></image>
            </div>

            <div>&nbsp</div>

            <Add ship_number={ship_number} company={company} handleShipNumber={a => handleShipNumber(a)} 
                handleCompany={a => handleCompany(a)} clickSearch={() => uploadParcel()} clickSave={()=> saveParcel()}/>

            <div class="col-6  mainPagecontainer ">
                <h2 class="blog-post-title">Pedidos</h2>

                <div class="aside">
                    <table>
                    {parcel_array.map((parcel, i) => {return (<Overview index = {i} number={parcel.shipnumber} 
                        company={parcel.company}/>)})}
                    </table>
                </div>   
            </div>

            <div>&nbsp</div>

            <div class="row">

                <div class="aside2">
                <button>Perfil</button>
                <button class="ghost" onclick="location.href='Login.html'">Cerrar sesi√≥n</button>
                </div>

            </div>
        </div>    


    );
  }


export default Home;
