import React, { useState, useEffect } from 'react';
import '../style.css';
import {Button} from 'react-bootstrap';


export default function Details(props) {

    function clickDetails(){
        props.clickDetails();
    }

      return (<div>
            <Button onClick={() => clickDetails()}>Detalles</Button>
            {props.details ? <div></div> : 
                            <div class="container" id="container">
                                <h2 align="center">DETALLES DEL PEDIDO</h2>
                                <br></br>
                                <h4>Peso: {props.shipment_weight} kg</h4>
                                <br></br>
                                <h4>País de Origen: {props.origin_country_iso3}</h4>
                                <br></br>
                                <h4>Día de recogida: {props.shipment_pickup_date}</h4>
                                <br></br>
                                <h4>Día de entrega: {props.shipment_delivery_date}</h4>
                            </div>}
        </div>
    );

}
