import React, { Component, useState, useEffect } from 'react';
import './style.css';
import { Link } from 'react-router-dom';
import { Button, Container } from 'react-bootstrap';
import {CryptoJS} from 'crypto-js';


function Login() {


    const PASSWORD = "";
    const EMAIL ="";
    const AUTH="";

    const [email, setEmail]= useState(EMAIL);
    const [password, setPassword]= useState(PASSWORD);
    const [auth, setAuth]= useState(AUTH);



        function decrypt(){
            var CryptoJS=require("crypto-js");
            var bytes = CryptoJS.AES.decrypt(password, 'KeyISST');
            var decryptedData = JSON.parse(bytes.toString(CryptoJS.enc.Utf8));
            setAuth = decryptedData;
        }



        async function comprobar(){
            const response = await fetch("/ships/" + email );
            const myjson = await response.json();
            var email = myjson.email;
            var encryptedPass = myjson.pass;
            decrypt(encryptedPass);
            if(password==auth){
                //ir a /home/email

            }
            else{
                //Mensaje de error
            }
        }










    return (



        <div class="login">

            <h2><img src={process.env.PUBLIC_URL + "/images/Logo.png"} ></img></h2>

            <div class="container" id="container">

                    <br></br>


                    <h2 align="center">Iniciar Sesión</h2>

                    <form>
                        <label>
                            <input type = "text" name ="email" placeholder='Email' value={email} onChange={e=>setEmail(e.target.value)} />
                        </label>


                    </form>




                    <form>
                        <label>
                            <input type="password" name="name" placeholder='Contraseña' value={password} onChange={e=>setPassword(e.target.value)} />
                        </label>
                    </form>

                    <h2>
                        <Button onClick={()=>decrypt()} align="center">INICIAR SESIÓN</Button>   
                    </h2>

                    <br></br>
                    <br></br>
                    <br></br>


                    <h2>¿Todavía no te has registrado?</h2>
                    <h5>Date de alta en nuestro servicio</h5>
                    <h2>
                        <Button  align="center" >Registrarse</Button>
                    </h2>







            </div>
        </div>    

    );
}

export default Login;
